import { FormControl, MenuItem, Select } from '@mui/material';
import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [governantes, setGovernantes] = useState([])
  // API
  // https://www.camara.leg.br/SitCamaraWS/Deputados.asmx/ObterDeputados

  // useEffect = executa um pedaço de código
  // com base em uma determinada condição
  useEffect(() => {
    // o código aqui dentro será executado uma vez
    // quando o componente carrega e não novamente
    // async -> enviar um pedido, espere por ele, faça algo com
    const getDeputadosData = async () => {
      await fetch("https://www.camara.leg.br/SitCamaraWS/Deputados.asmx/ObterDeputados")
        .then((response) => response.json())
        .then((data) => {
          // item1 .. retornando um objeto em uma forma
          // item2 .. retornando um objeto em uma forma
          const deputados = data.map((deputado) => ({
            name: deputado.deputado,
            value: deputado.dados.nome,
          }));
          setGovernantes(deputados);
        });
    };

    getDeputadosData
  }, []);

  return (
    <div className="app">
      <div className='app__header'>
        {/* Título */}
        <h1>Fiscal de Deputados</h1>
        <FormControl className="app__dropdown">
          {/* percorre todos os estados e mostra uma lista suspensa das opções */}
          <Select
            variant="outlined"
            value="select"
          >
            {
              governantes.map(governante => (
                <MenuItem value={governante.value}>{governante.name}</MenuItem>
              ))
            }
          </Select>
        </FormControl>
      </div>
